import re

def vowel_consonant_pattern(text):
    pattern = []
    vowels = 'aeiou'
    for char in text.lower():
        if char in vowels:
            pattern.append('V')
        elif char.isalpha():
            pattern.append('C')
        else:
            pattern.append(char)

    output = []
    current = pattern[0]
    count = 1

    for i in range(1, len(pattern)):
        if pattern[i] == current:
            count += 1
        else:
            output.append(f"{count}{current}")
            current = pattern[i]
            count = 1

    output.append(f"{count}{current}")

    return ''.join(output)

text = "The quick brown fox jumps over the lazy dog"
result = vowel_consonant_pattern(text)
print("\n".join(re.findall('.{1,5}', result)))

def tribonacci_sequence(n):
    if n == 0:
        return []
    if n == 1:
        return [0]
    if n == 2:
        return [0, 1]

    dp = [0] * n
    dp[0], dp[1], dp[2] = 0, 1, 1

    for i in range(3, n):
        dp[i] = dp[i - 1] + dp[i - 2] + dp[i - 3]

    return dp

def largest_non_palindrome(sequence):
    max_num = -1
    for number in sequence:
        str_num = str(number)
        if str_num != str_num[::-1] and number > max_num:
            max_num = number
    return max_num

