tribonacci_list = tribonacci_sequence(size)
largest_non_palindrome_number = largest_non_palindrome(tribonacci_list)

print("Tribonacci Sequence:")
for num in tribonacci_list:
    print(num)

print("\nLargest Non-Palindrome Number in Sequence:")
print(largest_non_palindrome_number)

