import re

def find_sequences(s):
    return re.findall(r'(?:\d+)(?=( \d+){3,})', s)

def is_arithmetic(seq):
    nums = list(map(int, seq.split()))
    if len(nums) < 2:
        return False
    diff = nums[1] - nums[0]
    return all((nums[i] - nums[i - 1]) == diff for i in range(2, len(nums)))

def is_geometric(seq):
    nums = list(map(int, seq.split()))
    if len(nums) < 2 or nums[0] == 0:
        return False
    ratio = nums[1] / nums[0]
    return all((nums[i] / nums[i - 1]) == ratio for i in range(2, len(nums)))

def main():
    text = "5 10 15 20 1 2 4 8 16 3 6 12 24 48 7 14 21 28 100 200 300"
    sequences = find_sequences(text)
    print(f"Found sequences: {sequences}")

    arithmetic_sequences = []
    geometric_sequences = []

    for seq in sequences:
        if is_arithmetic(seq):
            arithmetic_sequences.append(seq)
        if is_geometric(seq):
            geometric_sequences.append(seq)

    print("Arithmetic Sequences:")
    for seq in arithmetic_sequences:
        print(seq)

    print("Geometric Sequences:")
    for seq in geometric_sequences:
        print(seq)

main()

def min_coins(coins, amount):
    dp = [float('inf')] * (amount + 1)
    dp[0] = 0
    for coin in coins:
        for x in range(coin, amount + 1):
            dp[x] = min(dp[x], dp[x - coin] + 1)
    return dp[amount] if dp[amount] != float('inf') else -1

def play_game():
    levels = 10
    max_coins = 20
    results = []
    for level in range(1, levels + 1):
        coins = list(range(1, level + 1))
        amount = level * 5
        min_steps = min_coins(coins, amount)
        results.append((level, coins, amount, min_steps))
    return results

