import datetime

def encrypt_message(message, shift_map):
    encrypted = []
    for char in message:
        if char in shift_map:
            encrypted.append(shift_map[char])
        else:
            encrypted.append(char)
    return ''.join(encrypted)

def generate_shift_map(date):
    base_map = {chr(i): chr(i) for i in range(32, 127)}
    shift_value = int(date.strftime('%Y%m%d')) % 95
    shifted_map = {chr(32 + (i + shift_value) % 95): base_map[chr(32 + i)] for i in range(95)}
    return shifted_map

def generate_decrypted_messages(encrypted_message):
    today = datetime.date.today()
    decrypted_messages = {}
    for days_ago in range(10):
        date = today - datetime.timedelta(days=days_ago)
        shift_map = generate_shift_map(date)
        inverse_map = {v: k for k, v in shift_map.items()}
        decrypted_message = encrypt_message(encrypted_message, inverse_map)
        decrypted_messages[date.strftime('%Y-%m-%d')] = decrypted_message
    return decrypted_messages

message = "Hello, World! This is a test message."
today = datetime.date.today()
shift_map = generate_shift_map(today)
encrypted_message = encrypt_message(message, shift_map)
decrypted_messages = generate_decrypted_messages(encrypted_message)

for date, decrypted_message in decrypted_messages.items():
    print(f"Date: {date} -> {decrypted_message}")

