import statistics

def dynamic_moving_average(data, n):
    if n == 0:
        return []
    moving_averages = [0] * len(data)
    sum_values = 0
    for i in range(len(data)):
        if i < n:
            sum_values += data[i]
            moving_averages[i] = sum_values / (i + 1)
        else:
            sum_values += data[i] - data[i - n]
            moving_averages[i] = sum_values / n
    return moving_averages

