import math

def geometric_mean_recursive(arr, n=None, product=1):
    if n is None:
        n = len(arr)
    if n == 0:
        return product ** (1 / len(arr))
    else:
        print(f"Calculating: Current product = {product}, Current element = {arr[n-1]}")
        return geometric_mean_recursive(arr, n - 1, product * arr[n - 1])

