import math

def polygon_area(n, side_length):
    return (n * side_length ** 2) / (4 * math.tan(math.pi / n))

def recursive_polygon_areas(start_sides, end_sides, side_length):
    if start_sides > end_sides:
        return []
    current_area = polygon_area(start_sides, side_length)
    print(f"Sides: {start_sides}, Area: {current_area:.2f}")
    return [current_area] + recursive_polygon_areas(start_sides + 1, end_sides, side_length)

