from datetime import datetime, timedelta

def calculate_future_dates(start_date, months_to_add):
    if months_to_add == 0:
        return [start_date.strftime('%Y-%m-%d')]
    next_month_date = add_one_month(start_date)
    return [start_date.strftime('%Y-%m-%d')] + calculate_future_dates(next_month_date, months_to_add - 1)

def add_one_month(date):
    month = date.month
    year = date.year
    if month == 12:
        year += 1
        month = 1
    else:
        month += 1
    try:
        return date.replace(year=year, month=month)
    except ValueError:
        return date.replace(year=year, month=month, day=28)

