import math

def encode(message, key):
    encoded = []
    for i, char in enumerate(message):
        shift = key[i % len(key)]
        encoded_char = chr((ord(char) + shift - 32) % 95 + 32)
        encoded.append(encoded_char)
    return ''.join(encoded)

def decode(encoded_message, key):
    decoded = []
    for i, char in enumerate(encoded_message):
        shift = key[i % len(key)]
        decoded_char = chr((ord(char) - shift - 32) % 95 + 32)
        decoded.append(decoded_char)
    return ''.join(decoded)

def generate_key(length):
    return [(math.factorial(n) % 95) for n in range(length)]

message = "AdvancedEncryptionExample"
key_length = 10
key = generate_key(key_length)

encoded_message = encode(message, key)
decoded_message = decode(encoded_message, key)

for line in [f"Original: {message}", f"Encoded: {encoded_message}", f"Decoded: {decoded_message}"]:
    print(line)

