import math

def transform_data(data):
    transformed_data = []
    for i, x in enumerate(data):
        if i % 2 == 0:
            transformed_data.append(math.log1p(x) * math.sqrt(x))
        else:
            transformed_data.append(math.expm1(x) / (x + 1))
    return transformed_data

def create_output(data):
    output = []
    for i in range(1, len(data)):
        transformed_value = data[i] - data[i - 1]
        if transformed_value > 0:
            output.append(f"{i}: up by {transformed_value:.2f}")
        else:
            output.append(f"{i}: down by {abs(transformed_value):.2f}")
    return output

