import statistics

def recursive_median(data, index=0):
    if index == len(data):
        return data
    for i in range(index + 1, len(data)):
        if data[i] < data[index]:
            data[i], data[index] = data[index], data[i]
    print(f"Sorted data at index {index}: {data}")
    return recursive_median(data, index + 1)

def calculate_median(data):
    sorted_data = recursive_median(data)
    print(f"Final sorted data: {sorted_data}")
    return statistics.median(sorted_data)

