def max_consecutive_growth(dates, values):
    if not dates or not values or len(dates) != len(values):
        return 0

    n = len(values)
    dp = [0] * n
    dp[0] = 1
    max_growth = 1

    for i in range(1, n):
        if values[i] > values[i - 1] and (dates[i] - dates[i - 1]).days == 1:
            dp[i] = dp[i - 1] + 1
        else:
            dp[i] = 1
        max_growth = max(max_growth, dp[i])

    return max_growth

