def find_max_score(moves):
    if not moves:
        return 0

    scores = [0] * len(moves)
    scores[0] = moves[0]

    if len(moves) > 1:
        scores[1] = max(moves[0], moves[1])

    for i in range(2, len(moves)):
        scores[i] = max(scores[i - 1], scores[i - 2] + moves[i])

    return scores[-1]

