import statistics

def recursive_median(data, depth=0):
    print(f"{' ' * depth * 2}Depth {depth}: {data}")
    if len(data) <= 2:
        return statistics.median(data)
    med = statistics.median(data)
    lower_half = [x for x in data if x < med]
    upper_half = [x for x in data if x > med]
    new_data = lower_half + upper_half
    return recursive_median(new_data, depth + 1)

