def generate_maze_pattern(size):
    maze = [[' ' for _ in range(size)] for _ in range(size)]
    for i in range(size):
        for j in range(size):
            if (i + j) % 3 == 0:
                maze[i][j] = '#'
            elif (i - j) % 3 == 0:
                maze[i][j] = '*'
            elif (i % 2 == 0) or (j % 2 == 0):
                maze[i][j] = '+'

    return maze

