def max_coins(piles):
    n = len(piles)
    dp = [[0] * n for _ in range(n)]

    for length in range(2, n, 2):
        for i in range(n - length):
            j = i + length
            dp[i][j] = max(
                piles[i] + min(dp[i + 2][j], dp[i + 1][j - 1]),
                piles[j] + min(dp[i][j - 2], dp[i + 1][j - 1])
            )

    return dp[0][-1]

