from math import factorial
from statistics import mean, stdev

n = 10
probabilities = [0.1, 0.2, 0.3, 0.4]

def binomial_prob(n, k, p):
    return (factorial(n) // (factorial(k) * factorial(n - k))) * (p ** k) * ((1 - p) ** (n - k))

def expected_value(dist, n):
    return sum(k * dist[k] for k in range(n + 1))

def variance(dist, n, exp_val):
    return sum((k - exp_val) ** 2 * dist[k] for k in range(n + 1))

for p in probabilities:
    distribution = [binomial_prob(n, k, p) for k in range(n + 1)]
    exp_val = expected_value(distribution, n)
    var_val = variance(distribution, n, exp_val)
    std_dev = var_val ** 0.5
    print(f"Probability: {p}")
    print(f"Distribution: {[round(prob, 4) for prob in distribution]}")
    print(f"Expected Value: {round(exp_val, 4)}")
    print(f"Variance: {round(var_val, 4)}")
    print(f"Standard Deviation: {round(std_dev, 4)}")
    print("-" * 40)

