import math

class Polygon:
    def __init__(self, sides):
        self.sides = sides

    def perimeter(self):
        return sum(self.sides)

    def area(self):
        if len(self.sides) == 3:
            s = self.perimeter() / 2
            return math.sqrt(s * (s - self.sides[0]) * (s - self.sides[1]) * (s - self.sides[2]))
        elif len(self.sides) == 4:
            return self.sides[0] * self.sides[1]
        else:
            return None

    def is_regular(self):
        return len(set(self.sides)) == 1

polygons = [
    Polygon([3, 4, 5]),
    Polygon([5, 5, 5, 5]),
    Polygon([6, 6, 6]),
    Polygon([8, 6, 5, 7]),
    Polygon([10, 10, 10, 10]),
    Polygon([9, 9, 9, 9, 9, 9]),
    Polygon([7, 7, 7, 7, 7])
]

polygon_data = {}
for poly in polygons:
    if poly.is_regular():
        if 'Regular' not in polygon_data:
            polygon_data['Regular'] = []
        polygon_data['Regular'].append(poly)
    else:
        if 'Irregular' not in polygon_data:
            polygon_data['Irregular'] = []
        polygon_data['Irregular'].append(poly)

for category, polys in polygon_data.items():
    print(f"{category} Polygons:")
    for p in polys:
        print(f"Sides: {p.sides}, Perimeter: {p.perimeter()}, Area: {p.area()}")
    print()

