def poisson_distribution(lambd, k):
    return pow(lambd, k) * exp(-lambd) / factorial(k)

def generate_pattern(lambd, max_k):
    pattern = []
    for k in range(max_k + 1):
        pattern.append(round(poisson_distribution(lambd, k), 4))
    return pattern

