    for i in range(length):
        wave_height = math.ceil(math.sin(i / length * 2 * math.pi) * 5)
        if wave_height not in wave_pattern:
            wave_pattern[wave_height] = set()
        wave_pattern[wave_height].add(i)

    return wave_pattern

