import re

def encode_message(message):
    pattern = r'\b\w+\b'
    words = re.findall(pattern, message)
    encoded_words = [encode_word(word) for word in words]
    return ' '.join(encoded_words)

def encode_word(word):
    encoded = []
    for i, char in enumerate(word):
        if i % 2 == 0:
            encoded.append(chr(ord(char) + 2))
        else:
            encoded.append(chr(ord(char) - 1))
    return ''.join(encoded)

def decode_message(encoded_message):
    pattern = r'\b\w+\b'
    words = re.findall(pattern, encoded_message)
    decoded_words = [decode_word(word) for word in words]
    return ' '.join(decoded_words)

def decode_word(word):
    decoded = []
    for i, char in enumerate(word):
        if i % 2 == 0:
            decoded.append(chr(ord(char) - 2))
        else:
            decoded.append(chr(ord(char) + 1))
    return ''.join(decoded)

