from math import sqrt, floor

def board_positions(n):
    positions = [(x, y) for x in range(n) for y in range(n)]
    return {pos: True for pos in positions}

def knight_moves(pos, n):
    x, y = pos
    candidates = [
        (x + 2, y + 1), (x + 2, y - 1), (x - 2, y + 1), (x - 2, y - 1),
        (x + 1, y + 2), (x + 1, y - 2), (x - 1, y + 2), (x - 1, y - 2)
    ]
    return [(nx, ny) for nx, ny in candidates if 0 <= nx < n and 0 <= ny < n]

def calculate_score(position, board, n):
    moves = knight_moves(position, n)
    score = 0
    for move in moves:
        if board.get(move):
            score += sqrt(move[0]**2 + move[1]**2) % 10
    return score

def simulate_game(n):
    board = board_positions(n)
    scores = []

    for pos in board:
        if board[pos]:
            score = calculate_score(pos, board, n)
            scores.append((pos, floor(score)))

    return scores

n = 8
scores = simulate_game(n)
for position, score in scores:
    print(f"Position {position}: Score {score}")

