def is_perfect_square(x):
    s = int(math.sqrt(x))
    return s * s == x

def is_fibonacci(n):
    return is_perfect_square(5 * n * n + 4) or is_perfect_square(5 * n * n - 4)

def generate_fibonacci_like_sequence(limit):
    sequence = [i for i in range(2, limit) if is_fibonacci(i)]
