def tribonacci_modulo(n, m):
    if n == 0:
        return [0]
    if n == 1:
        return [0, 1]
    if n == 2:
        return [0, 1, 1]

    sequence = [0, 1, 1]
    for i in range(3, n + 1):
        next_value = (sequence[i - 1] + sequence[i - 2] + sequence[i - 3]) % m
        sequence.append(next_value)

    return sequence

