import re

def max_palindrome_subseq(text):
    n = len(text)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for cl in range(2, n + 1):
        for i in range(n - cl + 1):
            j = i + cl - 1
            if text[i] == text[j] and cl == 2:
                dp[i][j] = 2
            elif text[i] == text[j]:
                dp[i][j] = dp[i + 1][j - 1] + 2
            else:
                dp[i][j] = max(dp[i][j - 1], dp[i + 1][j])

    return dp

def extract_words(text):
    return re.findall(r'\b\w+\b', text.lower())

def main():
    text = """
    Dynamic programming is an algorithmic technique for solving problems by breaking them down into simpler subproblems.
    It is applicable when the solution can be recursively described in terms of solutions to subproblems.
    """
    words = extract_words(text)
    unique_words = sorted(set(words))
    results = []

    for word in unique_words:
        dp_table = max_palindrome_subseq(word)
        max_len = dp_table[0][len(word) - 1]
        results.append((word, max_len))

    results.sort(key=lambda x: (-x[1], x[0]))

    for word, length in results:
        print(f"{word}: {length}")

main()

