import re

def number_to_words(n):
    words = {0: "zero", 1: "one", 2: "two", 3: "three", 4: "four", 5: "five",
             6: "six", 7: "seven", 8: "eight", 9: "nine", 10: "ten",
             11: "eleven", 12: "twelve", 13: "thirteen", 14: "fourteen",
             15: "fifteen", 16: "sixteen", 17: "seventeen", 18: "eighteen",
             19: "nineteen", 20: "twenty", 30: "thirty", 40: "forty",
             50: "fifty", 60: "sixty", 70: "seventy", 80: "eighty", 90: "ninety"}
    if n <= 20:
        return words[n]
    elif n < 100:
        return words[n // 10 * 10] + ('' if n % 10 == 0 else '-' + words[n % 10])
    elif n < 1000:
        return words[n // 100] + ' hundred' + ('' if n % 100 == 0 else ' and ' + number_to_words(n % 100))

def sequence_word_length(start, end):
    sequence = [number_to_words(i) for i in range(start, end)]
    joined_sequence = " ".join(sequence)
    words_only = re.sub(r'\W+', '', joined_sequence)
    return len(words_only)

