from math import sqrt

def recursive_standard_deviation(data, n):
    if n == 1:
        return 0
    else:
        mean_n_minus_1 = recursive_mean(data, n - 1)
        mean_n = recursive_mean(data, n)
        deviation = data[n - 1] - mean_n_minus_1
        variance = recursive_variance(data, n - 1) + (deviation * (data[n - 1] - mean_n))
        return sqrt(variance / (n - 1))

def recursive_mean(data, n):
    if n == 1:
        return data[0]
    else:
        return ((n - 1) * recursive_mean(data, n - 1) + data[n - 1]) / n

def recursive_variance(data, n):
    if n == 1:
        return 0
    else:
        mean_n_minus_1 = recursive_mean(data, n - 1)
        mean_n = recursive_mean(data, n)
        deviation = data[n - 1] - mean_n_minus_1
        return recursive_variance(data, n - 1) + (deviation * (data[n - 1] - mean_n))

