from datetime import datetime, timedelta

def business_days_in_year(year):
    start_date = datetime(year, 1, 1)
    end_date = datetime(year, 12, 31)
    business_days = {0, 1, 2, 3, 4}
    current_date = start_date
    holidays = {
        datetime(year, 1, 1),
        datetime(year, 12, 25),
    }
    holidays.update(datetime(year, 12, 24) + timedelta(days=i) for i in range(7) if (datetime(year, 12, 24) + timedelta(days=i)).weekday() in business_days)
    day_count = 0
    while current_date <= end_date:
        if current_date.weekday() in business_days and current_date not in holidays:
            day_count += 1
        current_date += timedelta(days=1)
    return day_count

