def longest_increasing_subsequence(arr):
    if not arr:
        return []

    n = len(arr)
    lis = [1] * n
    prev = [-1] * n

    for i in range(1, n):
        for j in range(i):
            if arr[i] > arr[j] and lis[i] < lis[j] + 1:
                lis[i] = lis[j] + 1
                prev[i] = j

    max_len = max(lis)
    index = lis.index(max_len)

    sequence = []
    while index != -1:
        sequence.append(arr[index])
        index = prev[index]

    return sequence[::-1]

def transform_data(arr):
