from math import sqrt

def is_perfect_square(num):
    return int(sqrt(num)) ** 2 == num

def generate_fibonacci_upto(n):
    fib = [0, 1]
    while fib[-1] + fib[-2] < n:
        fib.append(fib[-1] + fib[-2])
    return fib

def is_square_in_fibonacci(num):
    fib_set = set(generate_fibonacci_upto(num))
    squares = {x ** 2 for x in range(1, int(sqrt(num)) + 1)}
    return fib_set.intersection(squares)

