import re

def is_valid(word):
    return bool(re.fullmatch(r"[a-zA-Z]{5}", word))

def calculate_score(word):
    vowels = 'aeiou'
    score = 0
    for char in word.lower():
        if char in vowels:
            score += 3
        else:
            score += 1
    return score

def game_logic(words):
    valid_words = [word for word in words if is_valid(word)]
    scores = [(word, calculate_score(word)) for word in valid_words]
    scores.sort(key=lambda x: x[1], reverse=True)
    return scores

