import math
from statistics import mean

data = {
    'A': [3, 7, 8, 9],
    'B': [10, 20, 30, 40],
    'C': [1, 4, 6, 8],
    'D': [15, 14, 13, 12]
}

def calculate_stats(data):
    stats = {}
    for key, values in data.items():
        avg = mean(values)
        variance = mean([(x - avg) ** 2 for x in values])
        stddev = math.sqrt(variance)
        stats[key] = (avg, variance, stddev)
        print(f"Processed {key}: Avg={avg}, Var={variance}, StdDev={stddev}")
    return stats

def compare_stats(stats):
    comparison = {}
    for key, (avg, variance, stddev) in stats.items():
        comparison[key] = set()
        for other_key, (other_avg, other_variance, other_stddev) in stats.items():
            if key != other_key:
                if avg < other_avg:
                    comparison[key].add('less_avg_than_' + other_key)
                if variance < other_variance:
                    comparison[key].add('less_var_than_' + other_key)
                if stddev < other_stddev:
                    comparison[key].add('less_stddev_than_' + other_key)
        print(f"Comparisons for {key}: {comparison[key]}")
    return comparison

def main():
    stats = calculate_stats(data)
    comparison = compare_stats(stats)
    for key, comparisons in comparison.items():
        print(f"{key}: {', '.join(comparisons)}")

main()

import math

def min_polygon_triangulation(points):
    n = len(points)
    dp = [[0] * n for _ in range(n)]

    def distance(p1, p2):
        return math.sqrt((p1[0] - p2[0]) ** 2 + (p1[1] - p2[1]) ** 2)

    def cost(i, j, k):
        return distance(points[i], points[j]) + distance(points[j], points[k]) + distance(points[k], points[i])

    for length in range(2, n):
        for i in range(n - length):
            j = i + length
            dp[i][j] = float('inf')
            for k in range(i + 1, j):
                q = dp[i][k] + dp[k][j] + cost(i, j, k)
                if q < dp[i][j]:
                    dp[i][j] = q
                    print(f"Updated dp[{i}][{j}] = {dp[i][j]} using k = {k}")

    return dp[0][n - 1]

polygon_points = [(0, 0), (2, 3), (4, 0), (6, 4), (8, 0)]
result = min_polygon_triangulation(polygon_points)
print(f"Minimum triangulation cost: {result}")

import re

def recursive_transform(data, depth=0):
    if isinstance(data, list):
        return [recursive_transform(item, depth + 1) for item in data]
    elif isinstance(data, dict):
        return {key: recursive_transform(value, depth + 1) for key, value in data.items()}
    elif isinstance(data, str):
        return re.sub(r'\d', lambda x: chr((ord(x.group()) - ord('0') + 97 + depth) % 122), data)
    elif isinstance(data, int):
        return data + depth
    else:
        return data

def generate_data(depth, current_depth=0):
    if current_depth == depth:
        return [str(i) for i in range(10)]
    return [
        generate_data(depth, current_depth + 1),
        {f"key_{i}": generate_data(depth, current_depth + 1) for i in range(3)}
    ]

