def max_sum_increasing_subsequence(arr):
    n = len(arr)
    dp = arr[:]
    sequence = [-1] * n
    max_sum_index = 0

    for i in range(1, n):
        for j in range(i):
            if arr[i] > arr[j] and dp[i] < dp[j] + arr[i]:
                dp[i] = dp[j] + arr[i]
                sequence[i] = j

    max_sum_index = max(range(n), key=lambda i: dp[i])
    return dp[max_sum_index], retrieve_sequence(arr, sequence, max_sum_index)

