import string

def pattern_encrypt(text):
    encoded_text = ""
    for i, char in enumerate(text):
        if char in string.ascii_letters:
            shift = (i % 5) + 1
            if char.islower():
                encoded_text += chr((ord(char) - ord('a') + shift) % 26 + ord('a'))
            else:
                encoded_text += chr((ord(char) - ord('A') + shift) % 26 + ord('A'))
        else:
            encoded_text += char
    return encoded_text

def generate_pattern(length):
    pattern = []
    for i in range(1, length + 1):
        pattern.append(pattern_encrypt(''.join(chr(65 + (j % 26)) for j in range(i))))
    return pattern

