import re

def find_and_replace(text, target, replacement):
    def recursive_replace(s, idx):
        if idx >= len(s):
            return s
        match = re.search(target, s[idx:])
        if not match:
            return s
        start, end = match.span()
        new_s = s[:idx + start] + replacement + s[idx + end:]
        return recursive_replace(new_s, idx + start + len(replacement))

    return recursive_replace(text, 0)

