def calculate_polygon_areas(vertices):
    areas = {}
    for name, points in vertices.items():
        n = len(points)
        area = 0
        for i in range(n):
            x1, y1 = points[i]
            x2, y2 = points[(i + 1) % n]
            area += x1 * y2 - x2 * y1
        areas[name] = abs(area) / 2
    return areas

