from datetime import datetime, timedelta

def is_leap_year(year):
    if year % 4 == 0:
        if year % 100 == 0:
            if year % 400 == 0:
                return True
            else:
                return False
        else:
            return True
    else:
        return False

def odd_days_in_month(year, month):
    if month in [4, 6, 9, 11]:
        return 30 % 7
    elif month == 2:
        return (29 % 7) if is_leap_year(year) else (28 % 7)
    else:
        return 31 % 7

def day_of_week_on_first_of_year(year):
    days_passed = 0
    for y in range(1, year):
        days_passed += 366 if is_leap_year(y) else 365
    return days_passed % 7

