        for month in range(1, 13):
            odd_days = odd_days_in_month(year, month)
            first_day_of_month = (first_day_of_year + sum(odd_days_in_month(year, m) for m in range(1, month))) % 7
            print(f"{'Jan Feb Mar Apr May Jun Jul Aug Sep Oct Nov Dec'.split()[month-1]}: {week_days[first_day_of_month]} ({odd_days} odd days)")
        print()

