import statistics

def recursive_mean(data, index=0, total=0):
    if index == len(data):
        return total / len(data) if data else 0
    print(f"Index: {index}, Current Total: {total}, Current Value: {data[index]}")
    return recursive_mean(data, index + 1, total + data[index])

def recursive_median(data):
    if len(data) == 0:
        return 0
    sorted_data = sorted(data)
    mid = len(sorted_data) // 2
    if len(sorted_data) % 2 == 0:
        return (sorted_data[mid - 1] + sorted_data[mid]) / 2
    else:
        return sorted_data[mid]

def recursive_mode(data):
    frequency = {}
    for num in data:
        if num in frequency:
            frequency[num] += 1
        else:
            frequency[num] = 1
    max_freq = max(frequency.values())
    modes = [key for key, value in frequency.items() if value == max_freq]
    return modes[0] if len(modes) == 1 else modes

def analyze_data(data):
    print("Analyzing data:", data)
    mean = recursive_mean(data)
    print("Mean calculated:", mean)
    median = recursive_median(data)
    print("Median calculated:", median)
    mode = recursive_mode(data)
    print("Mode calculated:", mode)
    return mean, median, mode

