def simulate_game_rounds(players, rounds):
    positions = {player: 0 for player in players}
    board = {
        0: 0, 1: 1, 2: -1, 3: 2, 4: 0, 5: -3,
        6: 3, 7: -2, 8: 4, 9: -4, 10: 5, 11: 0,
        12: -5, 13: 6, 14: -6, 15: 7, 16: 0, 17: -7,
        18: 8, 19: -8, 20: 9, 21: 0, 22: -9, 23: 10,
        24: -10
    }
    for _ in range(rounds):
        for player in players:
            move = (positions[player] + 3) % 25
            positions[player] = move + board[move]
    return positions

