import math

def tetrahedral_number(n):
    return n * (n + 1) * (n + 2) // 6

def generate_modified_sequence(limit):
    sequence = []
    for i in range(1, limit + 1):
        t_num = tetrahedral_number(i)
        if t_num % 2 == 0:
            sequence.append(t_num + math.isqrt(t_num))
        else:
            sequence.append(t_num - math.isqrt(t_num))
    return sequence

