def determine_winner(pattern):
    winner = None
    n = len(pattern)

    for i in range(n):
        if pattern[i] == 'X' * n:
            winner = 'X'
        elif pattern[i] == 'O' * n:
            winner = 'O'

    for j in range(n):
        column = ''.join(pattern[i][j] for i in range(n))
        if column == 'X' * n:
            winner = 'X'
        elif column == 'O' * n:
            winner = 'O'

    diag1 = ''.join(pattern[i][i] for i in range(n))
    diag2 = ''.join(pattern[i][n - i - 1] for i in range(n))

    if diag1 == 'X' * n or diag2 == 'X' * n:
        winner = 'X'
    elif diag1 == 'O' * n or diag2 == 'O' * n:
        winner = 'O'

    return winner

