import re
from datetime import datetime

def transform_dates(data, depth=0):
    if isinstance(data, dict):
        return {k: transform_dates(v, depth + 1) for k, v in data.items()}
    elif isinstance(data, list):
        return [transform_dates(elem, depth + 1) for elem in data]
    elif isinstance(data, str):
        match = re.match(r'\b(\d{4})-(\d{2})-(\d{2})\b', data)
        if match:
            year, month, day = map(int, match.groups())
            new_date = datetime(year, month, day).strftime('%d/%m/%Y')
            print(f"{'  ' * depth}Transformed {data} to {new_date}")
            return new_date
    return data

