import itertools

def generate_board(size):
    return [[None for _ in range(size)] for _ in range(size)]

def place_knight(board, pos, step):
    x, y = pos
    if board[x][y] is not None:
        return False
    board[x][y] = step
    return True

def knight_moves(size):
    moves = [(2, 1), (1, 2), (-1, 2), (-2, 1),
             (-2, -1), (-1, -2), (1, -2), (2, -1)]
    return moves

def is_within_bounds(size, pos):
    x, y = pos
    return 0 <= x < size and 0 <= y < size

def solve_knight_tour(size):
    board = generate_board(size)
    moves = knight_moves(size)
    start_pos = (0, 0)
    place_knight(board, start_pos, 1)

    def tour(step, pos):
        if step > size * size:
            return True
        for move in moves:
            new_pos = (pos[0] + move[0], pos[1] + move[1])
            if is_within_bounds(size, new_pos) and place_knight(board, new_pos, step):
                if tour(step + 1, new_pos):
                    return True
                board[new_pos[0]][new_pos[1]] = None
        return False

    if tour(2, start_pos):
        return board
    else:
        return None

def print_board(board):
    for row in board:
        print(" ".join(str(cell).rjust(2, ' ') if cell is not None else "__" for cell in row))

size = 5
solution = solve_knight_tour(size)
if solution:
    print_board(solution)
else:
    print("No solution found.")

