from statistics import mean

def moving_average(data, window_size):
    n = len(data)
    if window_size > n:
        return []
    dp = [0] * n
    dp[0] = data[0]
    for i in range(1, n):
        dp[i] = dp[i - 1] + data[i]
    averages = []
    for i in range(window_size - 1, n):
        if i == window_size - 1:
            sum_window = dp[i]
        else:
            sum_window = dp[i] - dp[i - window_size]
        averages.append(sum_window / window_size)
    return averages

