import math

def recursive_mean(data, index=0, total=0):
    if index == len(data):
        return total / len(data) if len(data) > 0 else 0
    return recursive_mean(data, index + 1, total + data[index])

def recursive_std_dev(data, mean=None, index=0, total=0):
    if mean is None:
        mean = recursive_mean(data)
    if index == len(data):
        variance = total / len(data) if len(data) > 0 else 0
        return math.sqrt(variance)
    return recursive_std_dev(data, mean, index + 1, total + (data[index] - mean) ** 2)

