def encrypt_message(message, shift):
    if not message:
        return ""
    first_char = message[0]
    if 'a' <= first_char <= 'z':
        shifted_char = chr((ord(first_char) - ord('a') + shift) % 26 + ord('a'))
    elif 'A' <= first_char <= 'Z':
        shifted_char = chr((ord(first_char) - ord('A') + shift) % 26 + ord('A'))
    else:
        shifted_char = first_char
    return shifted_char + encrypt_message(message[1:], shift)

def decrypt_message(encrypted_message, shift):
    if not encrypted_message:
        return ""
    first_char = encrypted_message[0]
    if 'a' <= first_char <= 'z':
        original_char = chr((ord(first_char) - ord('a') - shift) % 26 + ord('a'))
    elif 'A' <= first_char <= 'Z':
        original_char = chr((ord(first_char) - ord('A') - shift) % 26 + ord('A'))
    else:
        original_char = first_char
    return original_char + decrypt_message(encrypted_message[1:], shift)

