import math

def encode(text, shift, index=0):
    if index == len(text):
        return ""
    shifted_char = chr(((ord(text[index]) - 32 + shift) % 95) + 32)
    return shifted_char + encode(text, shift, index + 1)

def decode(encoded_text, shift, index=0):
    if index == len(encoded_text):
        return ""
    original_char = chr(((ord(encoded_text[index]) - 32 - shift) % 95) + 32)
    return original_char + decode(encoded_text, shift, index + 1)

def recursive_encryption(text, current_shift=0):
    if current_shift > 9:
        return []
    encoded_text = encode(text, current_shift)
    return [encoded_text] + recursive_encryption(text, current_shift + 1)

def recursive_decryption(encoded_texts, current_shift=0):
    if current_shift >= len(encoded_texts):
        return []
    decoded_text = decode(encoded_texts[current_shift], current_shift)
    return [decoded_text] + recursive_decryption(encoded_texts, current_shift + 1)

