import math

def text_similarity_score(text1, text2):
    def cosine_similarity(vec1, vec2):
        dot_product = sum(a * b for a, b in zip(vec1, vec2))
        magnitude1 = math.sqrt(sum(a**2 for a in vec1))
        magnitude2 = math.sqrt(sum(b**2 for b in vec2))
        return dot_product / (magnitude1 * magnitude2)

    def text_to_vector(text):
        words = re.findall(r'\w+', text.lower())
        return {word: words.count(word) for word in set(words)}

    def vectorize(text, all_words):
        text_vector = text_to_vector(text)
        return [text_vector.get(word, 0) for word in all_words]

    words1 = re.findall(r'\w+', text1.lower())
    words2 = re.findall(r'\w+', text2.lower())
    all_words = sorted(set(words1 + words2))

    vec1 = vectorize(text1, all_words)
    vec2 = vectorize(text2, all_words)

