from datetime import datetime, timedelta

def get_odd_days_of_month(year, month):
    date = datetime(year, month, 1)
    odd_days = []
    while date.month == month:
        if date.day % 2 != 0:
            odd_days.append(date.strftime('%Y-%m-%d'))
        date += timedelta(days=1)
    return odd_days

def calculate_day_of_week_frequencies(start_year, end_year):
    frequencies = {day: 0 for day in range(7)}
    for year in range(start_year, end_year + 1):
        for month in range(1, 13):
            odd_days = get_odd_days_of_month(year, month)
            for day in odd_days:
                day_of_week = datetime.strptime(day, '%Y-%m-%d').weekday()
                frequencies[day_of_week] += 1
    return frequencies

def find_least_frequent_days(frequencies):
    min_frequency = min(frequencies.values())
    least_frequent_days = [day for day, freq in frequencies.items() if freq == min_frequency]
    return least_frequent_days

